import React from "react";
import { ProductCategoryRow } from "./product-category-row";

export class ProductTable extends React.Component {
    constructor(props) {
        super(props);
        this.products = this.makeMap(props.products);
        console.log(this.products);
    }

    makeMap(products) {
        const productsArray = [products];
        let map = new Map();
        productsArray.forEach(element => {
            let newProduct = {
                'price': element.price,
                'stocked': element.stocked,
                'name': element.name
            }

            if (map.has(element.category)) {
                let values = map.get(element.category);
                values.push(newProduct);
                map.set(element.category, values);
            } else {
                let value = [];
                value.push(newProduct);
                map.set(element.category, value);
            }
        });

        return map;
    }

    render() {
        return (
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Price</th>
                    </tr>
                </thead>
                {this.products.map((product) =>
                    <ProductCategoryRow categoryName={product.category} products={product.category.items} />
                )}
            </table>
        )
    }
}
