import React from "react";
import { ProductCategoryRow } from "./product-category-row";

export class ProductTable extends React.Component {
    constructor(props) {
        super(props);
        this.data = this.makeMap(props.products);
    }

    makeMap(products) {
        let map = new Map();
        for (const item of products) {            
            let values = [];
            let newProduct = {
                'price': item.price,
                'stocked': item.stocked,
                'name': item.name
            }
            if (map.has(item.category)) {
                values = map.get(item.category);
            }
            values.push(newProduct);
            
            map.set(item.category, values);
        }

        console.log(map);

        return map;
    }

    render() {
        return (
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Price</th>
                    </tr>
                </thead>
                <tbody>
                    {[...this.data.keys()].length}
                    {
                    [...this.data.keys()].map(item => {
                        <p>{item}</p>
                        // <ProductCategoryRow categoryName={key} products={this.data.get(key)}/>
                    })}
                </tbody>
            </table>
        )
    }
}
