import { SearchBar } from "./search-bar";
import { ProductTable } from "./product-table";
import React from "react";

const allProducts = [
    {
        "category": "Sporting Goods",
        "price": "$49.99",
        "stocked": true,
        "name": "Football"
    },
    {
        "category": "Sporting Goods",
        "price": "$9.99",
        "stocked": true,
        "name": "Baseball"
    },
    {
        "category": "Sporting Goods",
        "price": "$29.99",
        "stocked": false,
        "name": "Basketball"
    },
    {
        "category": "Electronics",
        "price": "$99.99",
        "stocked": true,
        "name": "iPod Touch"
    },
    {
        "category": "Electronics",
        "price": "$399.99",
        "stocked": false,
        "name": "iPhone 5"
    },
    {
        "category": "Electronics",
        "price": "$199.99",
        "stocked": true,
        "name": "Nexus 7"
    }
];

export class FilterableProductTable extends React.Component {
    constructor(props) {
        super(props);
        this.state = { filterText: '', inStockOnly: false }
    }

    handleTermChange(value) {
        console.log(value);
        this.setState({ filterText: value });
    }

    handleStockOnlyChange(value) {
        console.log(value);
        this.setState({ inStockOnly: value });
    }

    render() {
        return (
            <div>
                <p>Simple search</p>
                <SearchBar
                    filterText={this.state.filterText}
                    inStockOnly={this.state.inStockOnly}
                    onSearchTermChange={this.handleTermChange}
                    onStockOnlyChange={this.handleStockOnlyChange} />
                <ProductTable filterText={this.state.filterText} inStockOnly={this.state.inStockOnly} products={allProducts} />
            </div>
        );
    }
};
