import React from 'react';

export class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.searchTerm = props.filterText;
        this.inStockOnly = props.inStockOnly;
        this.handleSearchTerm = this.handleSearchTerm.bind(this);
        this.handleInStockOnly = this.handleInStockOnly.bind(this);
    }

    handleSearchTerm(e)  {
        console.log('change term for search');
        this.searchTerm = e.target.value;
    }

    handleInStockOnly() {

    }

    render() {
        return (
            <div>
                <div>
                    <input type="text" value={this.searchTerm} onChange={this.handleSearchTerm}/>
                </div>
                <p>
                    <input type="checkbox" value={this.inStockOnly} onChange={this.handleInStockOnly}/>
                    <label>Only show products in stock</label>
                </p>
            </div>
        );
    }
};
