import React from "react";
import { ProductCategoryRow } from "./product-category-row";

export class ProductTable extends React.Component {
    constructor(props) {
        super(props);
        this.products = this.makeMap(props.products);
    }

    makeMap(products) {
        let map = new Map();
        products.array.forEach(element => {
            let newProduct = {
                'price': element.price,
                'stocked': element.stocked,
                'name': element.name
            }
            if (map.has(element.category)) {
                map[element.category].push(newProduct);
            } else {
                let value = [];
                value.push(newProduct);
                map.put(element.category, )
            }
        });

        return map;
    }

    render() {
        return (
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Price</th>
                    </tr>
                </thead>
                {this.products.map((product) => {
                    <ProductCategoryRow categoryName={product.category} products={product.category.items} />
                })}
            </table>
        )
    }
}
